INSERT INTO `pests` (`name_id`, `name_en`, `scientific_name`, `description_id`, `description_en`, `symptoms_id`, `symptoms_en`, `prevention_id`, `prevention_en`, `treatment_id`, `treatment_en`, `image_url`) VALUES
('Hama Putih Palsu', 'Rice Leaf Folder', 'Cnaphalocrocis medinalis', 'Larva menggulung daun padi dan memakan jaringan hijau di dalamnya, menyebabkan daun mengering dan berwarna putih.', 'Larvae roll rice leaves and feed on the green tissue inside, causing the leaves to dry and turn white.', 'Daun padi tergulung membujur, bagian dalam daun yang tergulung berwarna putih karena dimakan larva.', 'Rice leaves are rolled lengthwise, the inside of the rolled leaves are white due to larval feeding.', 'Sanitasi lingkungan, penggunaan musuh alami, rotasi tanaman.', 'Environmental sanitation, use of natural enemies, crop rotation.', 'Gunakan insektisida kontak atau sistemik dengan bahan aktif dimehipo atau tiametoksam.', 'Use contact or systemic insecticides with active ingredients like dimehypo or thiamethoxam.', '/images/pests/hama_putih_palsu.jpg'),
('Tikus Sawah', 'Rice Field Rat', 'Rattus argentiventer', 'Tikus adalah hama yang sangat merusak pada tanaman padi di semua fase pertumbuhan, terutama saat fase perkecambahan dan pemasakan biji. Mereka memakan bibit, batang, dan biji padi.', 'This pest attacks rice plants at all growth stages, especially during germination and grain ripening. They feed on seedlings, stems, and rice grains.', 'Kehilangan tanaman, lubang pada tanggul sawah, jalur jalan tikus di sekitar sawah, kerusakan pada batang dan malai padi.', 'Crop loss, holes in rice field dikes, rat runways around the fields, damage to rice stems and panicles.', 'Sanitasi lingkungan, penggunaan umpan beracun, gropyokan (perburuan tikus), penggunaan predator alami (burung hantu).', 'Environmental sanitation, use of poisonous bait, communal rat hunting, use of natural predators (owls).', 'Penggunaan rodentisida atau pemasangan perangkap di sekitar sawah.', 'Use of rodenticides or installation of traps around the fields.', '/images/pests/tikus_sawah.jpg'),
('Walang Sangit', 'Rice Bug', 'Leptocorisa oratorius', 'Walang sangit mengisap cairan pada bulir padi yang sedang masak susu, menyebabkan bulir menjadi hampa atau berwarna kehitaman dan kualitas beras menurun.', 'Rice bugs suck the fluid from developing rice grains, causing the grains to become empty or blackish and reducing rice quality.', 'Bulu padi menjadi hampa atau keriput, terdapat noda hitam pada bulir padi, bau sangit di sekitar area pertanaman.', 'Rice grains become empty or shriveled, black spots on rice grains, pungent odor around the planting area.', 'Tanam serentak, sanitasi gulma, penggunaan musuh alami.', 'Simultaneous planting, weed sanitation, use of natural enemies.', 'Gunakan insektisida kontak dengan bahan aktif fipronil atau klorpirifos.', 'Use contact insecticides with active ingredients like fipronil or chlorpyrifos.', '/images/pests/walang_sangit.jpg'),
('Wereng Hijau', 'Green Leafhopper', 'Nephotettix virescens', 'Wereng hijau adalah vektor penyakit tungro pada padi. Selain mengisap cairan tanaman, hama ini juga menyebarkan virus yang menyebabkan pertumbuhan tanaman terhambat dan kerdil.', 'Green leafhopper is a vector of tungro disease in rice. In addition to sucking plant sap, this pest also spreads viruses that cause stunted and dwarfed plant growth.', 'Tanaman kerdil, daun menguning atau oranye, terdapat bercak-bercak coklat pada daun.', 'Stunted plants, yellowing or orange leaves, brown spots on leaves.', 'Tanam varietas tahan tungro, sanitasi gulma, penggunaan insektisida bijaksana.', 'Plant tungro-resistant varieties, weed sanitation, judicious use of insecticides.', 'Pengendalian vektor dengan insektisida sistemik, cabut tanaman terinfeksi tungro.', 'Vector control with systemic insecticides, uproot tungro-infected plants.', '/images/pests/wereng_hijau.jpg');
